<div id="notesDialog" class="dialog" title="Событие">	<form>		<div class="widget fluid" style="margin-top: 0px;">			<div class="formRow">				<div class="grid4"><label>Текст события:</label></div>				<div class="grid8">					<input id="event_type" type="text" />				</div>				<div class="clear"></div>			</div>			<div class="formRow">				<div class="grid3">					<label>Начало:</label>				</div>				<div class="grid9">					<input id="event_start" type="text" />				</div>				<div class="clear"></div>			</div>			<div class="formRow">				<div class="grid3">					<label>Конец:</label>				</div>				<div class="grid9">					<input id="event_end" type="text" />				</div>				<div class="clear"></div>			</div>			<input id="event_id" type="hidden" />		</div>	</form></div>